mapper.select:
select 

from nmpl_company_heartbeat
where source_id = #{sourceId,jdbcType=VARCHAR}
  and target_id = #{targetId,jdbcType=VARCHAR}
  and company_network_id = #{companyNetworkId,jdbcType=VARCHAR}

mapper.delete:
delete from nmpl_company_heartbeat
where source_id = #{sourceId,jdbcType=VARCHAR}
  and target_id = #{targetId,jdbcType=VARCHAR}
  and company_network_id = #{companyNetworkId,jdbcType=VARCHAR}

mapper.insert:
