mapper.select:
SELECT a.sum_number, a.data_item_code, a.upload_time, a.company_network_id FROM nmpl_data_collect as a, ( SELECT max(upload_time) AS q, data_item_code, company_network_id FROM nmpl_data_collect GROUP BY data_item_code, company_network_id ) AS t WHERE a.upload_time = t.q and a.data_item_code = #{dataItemCode,jdbcType=VARCHAR} and t.data_item_code = #{dataItemCode,jdbcType=VARCHAR} and t.company_network_id = a.company_network_id
and t.company_network_id = #{companyNetworkId,jdbcType=VARCHAR}
and a.company_network_id = #{companyNetworkId,jdbcType=VARCHAR}

select a.sum_number, a.data_item_code, a.upload_time, a.company_network_id from nmpl_data_collect as a where a.data_item_code = #{dataItemCode,jdbcType=VARCHAR}
and a.company_network_id = #{companyNetworkId,jdbcType=VARCHAR}
and ( SELECT FROM_UNIXTIME(UNIX_TIMESTAMP(CAST(SYSDATE()AS DATE) - INTERVAL 1 DAY)) <= a.upload_time ) and ( SELECT FROM_UNIXTIME(UNIX_TIMESTAMP(CAST(SYSDATE() AS DATE))) >= a.upload_time )
