mapper.select:
select id,station_id, station_name, station_type,station_status,
nmpl_base_station_info.remark, public_network_ip,public_network_port,
lan_ip, SUBSTRING_INDEX(station_network_id,'-',-1) as station_network_id,lan_port,
relation_operator_id, nick_name,company_name
from nmpl_base_station_info,nmpl_company_info,nmpl_user

  nmpl_base_station_info.is_exist = 1
  and nmpl_company_info.is_exist = 1
  and nmpl_user.is_exist = 1
  and station_status in(2,3,4)

    and station_id = #{stationId,jdbcType=VARCHAR}


    and station_name = #{stationName,jdbcType=VARCHAR}


    and relation_operator_id = #{relationOperatorId,jdbcType=VARCHAR}


    and station_type = #{stationType,jdbcType=VARCHAR}


    and lan_ip = #{lanIp,jdbcType=CHAR}


    and station_network_id = #{stationNetworkId,jdbcType=CHAR}

  and nmpl_base_station_info.relation_operator_id = nmpl_company_info.company_id
  and nmpl_user.user_id = nmpl_base_station_info.create_user

order by nmpl_base_station_info.update_time desc

select id,station_id, station_name, station_type,station_status, remark, public_network_ip,public_network_port, lan_ip, station_network_id,lan_port, relation_operator_id, nick_name,company_name from( select id,station_id, station_name, station_type,station_status, nmpl_base_station_info.remark, public_network_ip,public_network_port, lan_ip, SUBSTRING_INDEX(station_network_id,'-',-1) as station_network_id,lan_port, relation_operator_id, nick_name,company_name from nmpl_base_station_info,nmpl_company_info,nmpl_user
nmpl_base_station_info.is_exist = 1 and nmpl_company_info.is_exist = 1
and station_id = #{stationId,jdbcType=VARCHAR}
and station_name like concat ('%',#{stationName,jdbcType=VARCHAR},'%')
and relation_operator_id = #{relationOperatorId,jdbcType=VARCHAR}
and station_type = #{stationType,jdbcType=VARCHAR}
and lan_ip = #{lanIp,jdbcType=CHAR}
and public_network_ip = #{publicNetworkIp,jdbcType=VARCHAR}
and nmpl_base_station_info.relation_operator_id = nmpl_company_info.company_id and nmpl_user.user_id = nmpl_base_station_info.create_user and nmpl_company_info.company_type = '02'
order by nmpl_base_station_info.update_time desc ) as temp
and station_network_id like concat ('%',#{stationNetworkId,jdbcType=CHAR},'%')

select id,station_id, station_name, station_type,station_status,
nmpl_base_station_info.remark, public_network_ip,public_network_port,
lan_ip, SUBSTRING_INDEX(station_network_id,'-',-1) as station_network_id,lan_port,
relation_operator_id, nick_name,company_name
from nmpl_base_station_info,nmpl_company_info,nmpl_user

  nmpl_base_station_info.is_exist = 1
  and nmpl_company_info.is_exist = 1
  and nmpl_user.is_exist = 1

    and station_id = #{stationId,jdbcType=VARCHAR}


    and station_name = #{stationName,jdbcType=VARCHAR}


    and relation_operator_id = #{relationOperatorId,jdbcType=VARCHAR}


    and station_type = #{stationType,jdbcType=VARCHAR}


    and lan_ip = #{lanIp,jdbcType=CHAR}


    and station_network_id = #{stationNetworkId,jdbcType=CHAR}

  and nmpl_base_station_info.relation_operator_id = nmpl_company_info.company_id
  and nmpl_user.user_id = nmpl_base_station_info.create_user

order by nmpl_base_station_info.update_time desc

select station_id,station_name from nmpl_base_station_info where is_exist = 1 and relation_operator_id = #{relationOperatorId,jdbcType=VARCHAR}
union
select device_id as station_id,device_name as station_name from nmpl_device_info where is_exist = 1 and device_type != '20' and relation_operator_id = #{relationOperatorId,jdbcType=VARCHAR}
